<?php
// Exit if accessed directly
if (!defined('ABSPATH'))
	exit;


if (!function_exists('azo_get_current_url')) {

	/**
	 * Get the current URL.
	 *
	 * This function constructs the current URL by checking if the connection is HTTPS, 
	 * and then combining the protocol, host, and request URI. It returns the complete URL as a string.
	 *
	 * @return string The current URL.
	 */
	function azo_get_current_url()
	{
		$is_https = isset($_SERVER['HTTPS']) && sanitize_text_field(wp_unslash($_SERVER['HTTPS'])) === 'on';
		$protocol = $is_https ? 'https' : 'http';
		$host = sanitize_text_field(wp_unslash($_SERVER['HTTP_HOST']));
		$uri = sanitize_text_field(wp_unslash($_SERVER['REQUEST_URI']));
		$current_url = $protocol . '://' . $host . $uri;
		return $current_url;
	}
}

/**
 * Return classes for dashboard icons.
 * @param  string $class class name
 * @return string       return class.
 *
 * @since 1.0.0
 */
function azo_pretty_class($class)
{

	if ("Google+" === $class) {
		return 'azo_google_plus';
	} else if ("(not set)" === $class) {
		return "azo_not_set";
	}

	return "azo_" . azo_transliterateString(str_replace(array("’", "‘", ' & ', '-', ' '), '_', strtolower($class)));
}

