/**
 * This function is used to fetch the profile name, UA Code from selected account/property.
 *
 * @param string|int $id    - Profile ID.
 * @param string     $index - Type of info wanted.
 * @return string | null
 */
function azo_search_profile_info($id, $index)
{

	if (!get_option('azo_google_token')) {
		return;
	}

	$ga_properties = get_option('azo_profiles_list_summary');

	if (empty($ga_properties)) {
		return;
	}

	foreach ($ga_properties as $account => $properties) {
		foreach ($properties as $property_id => $property) {
			if ($property_id == $id) {
				switch ($index) {
					case 'webPropertyId':
						return $property['code'];
					case 'websiteUrl':
						return $property['website_url'];
					case 'name':
						return $property['name'];
					case 'accountId':
						return $property['property_id'];
					case 'internalWebPropertyId':
						return $property['web_property_id'];
					case 'viewId':
						return $property['view_id'];
					default:
						return '';
				}
			}
		}
	}

	return '';
}


/**
 * Get the Google Analytics report URL.
 *
 * This function constructs a Google Analytics report URL by prefixing the reporting property ID 
 * with 'p'. It retrieves the reporting property ID using the `AZO_Utils::get_reporting_property` method.
 *
 * @param string $dashboard_profile_ID The dashboard profile ID (not used in the function).
 * @return string The constructed Google Analytics report URL.
 */
function azo_get_ga_report_url($dashboard_profile_ID)
{
	return 'p' . AZO_Utils::get_reporting_property();
}
